$(function(){$(".linkscroll[href*=#]:not([href=#])").click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")||location.hostname==this.hostname){var e=$(this.hash);var t=parseInt($(this).attr("data-speed"));e=e.length?e:$("[name="+this.hash.slice(1)+"]");if(e.length){$("html,body").animate({scrollTop:e.offset().top},t);return false}}});$(".topscroll").click(function(){var e=parseInt($(this).attr("data-speed"));$("html, body").animate({scrollTop:0},e);return false})})