$(function(){$(".linkscroll[href*=#]:not([href=#])").click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")||location.hostname==this.hostname){var e=$(this.hash);e=e.length?e:$("[name="+this.hash.slice(1)+"]");if(e.length){$("html,body").animate({scrollTop:e.offset().top},800);return false}}});$(".topscroll").click(function(){$("html, body").animate({scrollTop:0},800);return false})});